var link=document.querySelector(".cont-btn"),popup=document.querySelector(".popup"),close=popup.querySelector(".close"),form=popup.querySelector(".form"),name=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("name");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("popup-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("popup-show"),popup.classList.remove("popup-error")}),form.addEventListener("submit",function(a){name.value&&email.value?localStorage.setItem("name",name.value):(a.preventDefault(),popup.classList.remove("popup-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("popup-error"))}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("popup-show")&&(popup.classList.remove("popup-show"),popup.classList.remove("popup-error"))});